import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import TodosList from './components/todos-list.component';
import CreateTodo from './components/create-todo.component';
import EditTodo from './components/edit-todo.component';
import logo from "./logo.png";

class App extends Component {
  render() {
    return (
      <Router> 

        <div className="container">

          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <a className="navbar-brand" href="https.codingthesmartway.com" target="_black">
              <img src={logo} width="30" height="30" alt="Codingthemsartway.com" />
            </a>
            <Link to="/" className="navbar-brand"> MERN Stack Todo App</Link>
            <div className="collapse nav-collapse">
              <ul className="navbar-nav mr-auto">
                <li className="navbar-item">
                  <Link to="/" className="nav-link">Todos</Link>
                  <li className="navbar-item">
                  <Link to="/create" className="nav-link">Create Todo</Link>
                  </li> <li className="navbar-item">
                  <Link to="/edit" className="nav-link">Edit Todo</Link>
                  </li>
                </li>
              </ul>
            </div>
          </nav>


          <Route path="/" exact component={TodosList} />
          <Route path="/edit/:id" component={EditTodo} />
          <Route path="/create" component={CreateTodo} />
        </div>

      </Router>
    );
  }
}

export default App;
